catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	//Get the values of the date variables 
  var dateNeeded = g_form.getValue('date_needed');
  var dateToReturn = g_form.getValue('date_to_return');

  // Convert the date values to Date objects for comparison
  var dateNeededObj = new Date(dateNeeded);
  var dateToReturnObj = new Date(dateToReturn);

  // Compare the dates
  if (dateToReturnObj < dateNeededObj) {
    // Display an error message
    g_form.addErrorMessage("The Date to Return cannot be before the Date Needed.");
	g_form.showErrorBox('date_to_return','The Date to Return cannot be before the Date Needed','Error message');
 g_form.setValue('date_to_return', '');
  }
}
