sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Search for existing ticket associated to the current tracker record open
    var vehService = new GlideRecord('x_848642_loaner_0_vehicle_servicing');
    vehService.addQuery('vehicle_tracker', current.sys_id);
    vehService.addQuery('tracker_status', 'Open');
    vehService.query();
	if (!vehService.next()) {
        // create new ticket if not found
        var createticket = new GlideRecord('x_848642_loaner_0_vehicle_servicing');

        // Current date service ticket was opened
        var gd = new GlideDate();
        // populate required
        createticket.initialize();
        createticket.setValue('vehicle_tracker', current.sys_id);
        createticket.setValue('u_reference_2', current.vehicle);
        createticket.setValue('u_choice_3', 'In Service');
        createticket.setValue('date_sent_for_servicing', gd.getValue());
        createticket.setValue('ticket_status', 'Open');
        createticket.insert();
    }

})(current, previous);
